from django.shortcuts import render
import random
# Create your views here.

def drink(request):
    drinks = ['솔의눈','갈아만든배','알로에','제주감귤']
    drink = random.choice(drinks)
    if drink == '솔의눈':
        img = 'data:image/jpeg;base64,'
    elif drink == '갈아만든배':
        img = 'data:image/jpeg;base64,'
    elif drink == '알로에':
        img = 'https://img3.yna.co.kr/etc/inner/KR/2016/12/06/AKR20161206052500030_01_i_P2.jpg'
    else :
        img = 'https://simg.ssgcdn.com/trans.ssg?src=/cmpt/edit/202005/11/132020051113381154514539244453_750.jpg&w=830&t=a39c075f237d4f4b1321cc7cf1b7e5c77aceb572'
    context = {
        'drink' : drink,
        'img' : img,
    }
    return render(request, 'drink.html', context)